    if (UpperSetpoint < Setpoint 
        && UpperSetpoint - UpperTemperature < MaxTempDiff 
        && LowerSetpoint - LowerTemperature < MaxTempDiff) {
        UpperSetpoint = UpperSetpoint + DeltaTemp;
        LowerSetpoint = UpperSetpoint;
    }
    //if (LowerSetpoint < Setpoint && LowerSetpoint - LowerTemperature < MaxTempDiff) LowerSetpoint = LowerSetpoint + DeltaTemp;
} else if (RampDirection == 'Decreasing') {
    if (UpperSetpoint > Setpoint 
        && UpperTemperature - UpperSetpoint < MaxTempDiff 
        && LowerTemperature - LowerSetpoint < MaxTempDiff) {
        UpperSetpoint = UpperSetpoint - DeltaTemp;
        LowerSetpoint = UpperSetpoint;
    }
    //if (LowerSetpoint > Setpoint && LowerTemperature - LowerSetpoint < MaxTempDiff) LowerSetpoint = LowerSetpoint - DeltaTemp;
} else {
    // uh oh...
