var selected_input = global.get('analoginput.selected_input')
var input = global.get('analoginput.input['+selected_input+']')

try {
    // m = y2*y1/x2*x1
    input.calibration.span = (input.calibration.y.max - input.calibration.y.min) / (input.calibration.x.max - input.calibration.x.min)

    // b = y-mx
    input.calibration.offset = input.calibration.y.max - input.calibration.span * input.calibration.x.max

} catch (error) {
    node.warn(error)
};

global.set('analoginput.input[' + selected_input + ']', input)

return msg;

